import React from "react";
import { render } from "react-dom";

var isLoggedIn = false;

function renderConditionally() {
  if (isLoggedIn) {
    return <h1 > Hello < /h1>
  } else {
    return ( <
      form className = "form" >
      <
      input type = "text"
      placeholder = "Username" / >
      <
      input type = "password"
      placeholder = "Password" / >
      <
      button type = "submit" > Login < /button> <
      /form>
    )
  }
}

function App() {
  return (
    <div className="container">
      {renderConditionally()}
    </div>
  );
}

export default App;
